/**
 * Type of option used in JS side
 */
declare type ColorPickerInputOptions = {
    /**
     * whether alpha is supported or not.
     * if true, user can chose the opacity of the color
     * @default false
     */
    supportsAlpha?: boolean;
    /**
     * initial color displayed on the picker
     */
    initialColor?: string;
};
declare const ColorPicker: {
    showColorPicker: (options?: ColorPickerInputOptions | undefined, callback?: ((color: string) => void) | undefined) => void;
};
export default ColorPicker;
